def PatternMatching(Pattern, Genome):
    positions=[]
    l = len(Pattern)
    g = len(Genome)
    for i in range(0,g):
        if (Genome[i:i+l] == Pattern) :
            positions.append(i)

    return positions

pattern = 'CTCCGCTCT'
genome = ''
print(*PatternMatching(pattern, genome), sep = ' ')
